const productReducer = (state: ProductState, action: ProductAction): ProductState => {
  switch (action.type) {
    case "ADD_PRODUCT":
      return { products: [...state.products, action.payload] };
    case "UPDATE_PRODUCT":
      return {
        products: state.products.map(p =>
          p.id === action.payload.id ? action.payload : p
        ),
      };
    case "DELETE_PRODUCT":
      return { products: state.products.filter(p => p.id !== action.payload) };
    default:
      return state;
  }
};

export const ProductContext = createContext<{
  products: Product[];
  addProduct: (p: Product) => void;
  updateProduct: (p: Product) => void;
  deleteProduct: (id: number) => void;
} | null>(null);

export const ProductProvider = ({ children }: { children: ReactNode }) => {
  const [state, dispatch] = useReducer(productReducer, {
    products: initialProducts,
  });

  const addProduct = (p: Product) => dispatch({ type: "ADD_PRODUCT", payload: p });
  const updateProduct = (p: Product) => dispatch({ type: "UPDATE_PRODUCT", payload: p });
  const deleteProduct = (id: number) => dispatch({ type: "DELETE_PRODUCT", payload: id });

  return (
    <ProductContext.Provider value={{ products: state.products, addProduct, updateProduct, deleteProduct }}>
      {children}
    </ProductContext.Provider>
  );
};